import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { getSongs } from '../actions/songActions';
import CardList from './CardList';
import './Home.css';

const genreList = [
	{ genre: 'populara',
		totalSongs: 4,
	},
	{ genre: 'folclor',
		totalSongs: 2,
	},
	{ genre: 'rock',
		totalSongs: 0,
	},
	{ genre: 'de petrecere',
		totalSongs: 4,
	},
	{ genre: 'usoara',
		totalSongs: 3,
	},
	{ genre: 'metal',
		totalSongs: 1,
	},
	{ genre: 'pop',
		totalSongs: 5,
	},
	{ genre: 'jazz',
		totalSongs: 7,
	},
	{ genre: 'folk',
		totalSongs: 4,
	},
]

const navigateToCard = () => {
	console.log('......... navigate to card!!!');
}
const handleSongAdd = () => {
	console.log('......... songgggg ADDDDDDDD!!!');
}

const Home = () => {
// export class Page extends Component {
	// static propTypes = {
	// 	getSongs: PropTypes.func.isRequired,
	// 	song: PropTypes.object.isRequired,
	// }
	return (
		<>
			<CardList
				items={genreList}
				onCardClick={navigateToCard()}
			/>
			<div className='buttonContainer'>
				<button className="addButton" onClick={handleSongAdd}>+</button>
			</div>
		</>
	)
};

const mapStateToProps = state => ({
	song: state.song
});

export default connect(mapStateToProps, { getSongs })(Home);
